package cn.xqhuang.dps.algorithms.solution;

import java.util.ArrayList;
import java.util.List;

/**
 * 超过时间限制, 使用二分法
 *
 * @author huangxiangquan@yintatech.com
 * @date 2025/01/10 09:41
 */
public class Solution_3297 {

    public static long validSubstringCount(String word1, String word2) {
        int length1 = word1.length();
        int length2 = word2.length();
        long res = 0;
        for (int i = 0; i < length1 - length2 + 1; i++) {
            List<String> chars = new ArrayList<>();
            for (int x = 0; x < length2; x++) {
                chars.add(String.valueOf(word2.charAt(x)));
            }
            for (int j = i; j < length1; j++) {
                chars.remove(String.valueOf(word1.charAt(j)));
                if (chars.isEmpty()) {
                    res += (length1 - j);
                    break;
                }
            }
        }

        return res;
    }

    public static void main(String[] args) {
        System.out.println(validSubstringCount("", ""));
    }
}
